mapper.update:
update sys_merchant
set amount = amount + #{amount}
where merchant_id = #{merchantId}
  and amount_index = 0

update sys_merchant
set amount = amount + #{amount}
where merchant_id = #{merchantId}
  and amount_index = FLOOR(RAND() * 10)

mapper.select:
select sum(amount)
from sys_merchant
where merchant_id = #{merchantId}
